envioModule.factory("notificationFactory",function(){return{success:function(t){toastr.options={positionClass:"toast-top-right",closeButton:!0},toastr.success(t,"¡Éxito!")},successTopFull:function(t){toastr.options={positionClass:"toast-top-full-width",closeButton:!0},toastr.success(t,"¡Éxito!")},error:function(t){toastr.options={positionClass:"toast-top-right",closeButton:!0},toastr.error(t,"Error")},info:function(t){toastr.options={positionClass:"toast-top-right",closeButton:!0},toastr.info(t,"Información")},infoTopFull:function(t){toastr.options={positionClass:"toast-top-full-width",closeButton:!0},toastr.info(t,"Información")},warning:function(t){toastr.options={positionClass:"toast-top-right",closeButton:!0},toastr.warning(t,"Atención")}}});